package bindings

import (
	"encoding/hex"
	"fmt"

	"github.com/umbracle/go-web3/abi"
)

var abiMintERC20 *abi.ABI

// MintERC20Abi returns the abi of the MintERC20 contract
func MintERC20Abi() *abi.ABI {
	return abiMintERC20
}

var binMintERC20 []byte

// MintERC20Bin returns the bin of the MintERC20 contract
func MintERC20Bin() []byte {
	return binMintERC20
}

func init() {
	var err error
	abiMintERC20, err = abi.NewABI(abiMintERC20Str)
	if err != nil {
		panic(fmt.Errorf("cannot parse MintERC20 abi: %v", err))
	}
	if len(binMintERC20Str) != 0 {
		binMintERC20, err = hex.DecodeString(binMintERC20Str[2:])
		if err != nil {
			panic(fmt.Errorf("cannot parse MintERC20 bin: %v", err))
		}
	}
}

