package bindings

import (
	"encoding/hex"
	"fmt"

	"github.com/umbracle/go-web3/abi"
)

var abiValidator *abi.ABI

// ValidatorAbi returns the abi of the Validator contract
func ValidatorAbi() *abi.ABI {
	return abiValidator
}

var binValidator []byte

// ValidatorBin returns the bin of the Validator contract
func ValidatorBin() []byte {
	return binValidator
}

func init() {
	var err error
	abiValidator, err = abi.NewABI(abiValidatorStr)
	if err != nil {
		panic(fmt.Errorf("cannot parse Validator abi: %v", err))
	}
	if len(binValidatorStr) != 0 {
		binValidator, err = hex.DecodeString(binValidatorStr[2:])
		if err != nil {
			panic(fmt.Errorf("cannot parse Validator bin: %v", err))
		}
	}
}

var binValidatorStr = ""

var abiValidatorStr = `[
    {
      "inputs": [
        {
          "internalType": "address[]",
          "name": "_validators",
          "type": "address[]"
        }
      ],
      "stateMutability": "nonpayable",
      "type": "constructor"
    },
    {
      "inputs": [],
      "name": "getValidators",
      "outputs": [
        {
          "internalType": "address[]",
          "name": "_validators",
          "type": "address[]"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "address[]",
          "name": "_validators",
          "type": "address[]"
        }
      ],
      "name": "setValidators",
      "outputs": [],
      "stateMutability": "payable",
      "type": "function"
    }
  ]`
